(function(){"use strict";if(window.innerWidth>=768){var intFrameHeight=window.innerHeight;var elAnimation=document.querySelectorAll("[data-animation]"),countContainer=elAnimation.length;var animateInViewport=function(){for(var i=0;i<countContainer;++i){var pos=elAnimation[i].getBoundingClientRect(),rounded=Math.round(pos.top)-100;if(rounded<=intFrameHeight){elAnimation[i].classList.add("animate")}}};var startAnimation=function(){animateInViewport()};animateInViewport()}window.addEventListener("scroll",function(){requestAnimationFrame(startAnimation)},{capture:true,passive:true})})();